import * as React from "react";
import * as Web3 from "web3";
import getWeb3 from "./util/getWeb3";

const appStyles = require("./App.css");
const logo = require("./logo.svg");

import MetaWallet from "./components/MetaWallet";

interface IAppState {
  web3: Web3;
}

class App extends React.Component<{}, IAppState> {
  constructor(props) {
    super(props);
    this.state = {
      web3: null,
    };
  }

  public async componentWillMount() {
    const web3 = await getWeb3();
    this.setState({
      web3,
    });
  }

  public render() {
    return (
      <div className={appStyles.app}>
        <div className={appStyles.appHeader}>
          <img src={logo} className={appStyles.appLogo} alt="logo" />
          <h2>React Ethereum DApp Template</h2>
        </div>
        <div className={appStyles.appIntro}>
          {this.state.web3 ? (
            <div>
              <p>
                Provider is MetaMask: {(this.state.web3.currentProvider as any).isMetaMask ? "yes" : "no"}
              </p>
              <p>
                Provider is Mist: {(window as any).mist ? "yes" : "no"}
              </p>
              {(this.state.web3.currentProvider as any).host ?
                <p>Provider is {(this.state.web3.currentProvider as any).host}</p> : null}
            </div>
          ) :
            <p>Web3 is loading</p>}
        </div>
        <hr />
        {this.state.web3 ? <MetaWallet web3={this.state.web3} /> : null}
      </div>
    );
  }
}

export default App;

import * as React from "react";
import * as TruffleContract from "truffle-contract";
import * as Web3 from "web3";

const MetaCoinContract = TruffleContract(require("../../build/contracts/MetaCoin.json"));
import IMetaCoin from "../contract-interfaces/IMetaCoin";

interface IMetaWalletProps {
  web3: Web3;
}

interface IMetaWalletState {
  account: string;
  accountError: boolean;
  balance: string;
  contractAddress: string;
}

export default class MetaWallet extends React.Component<IMetaWalletProps, IMetaWalletState> {
  constructor(props) {
    super(props);
    this.state = {
      account: "",
      accountError: false,
      balance: "",
      contractAddress: "",
    };
  }

  public async componentWillMount() {
    if (this.props.web3.eth.accounts.length === 0) {
      this.setState({
        account: "",
        accountError: true,
      });
      return;
    }
    MetaCoinContract.setProvider(this.props.web3.currentProvider);
    let instance: IMetaCoin;
    try {
      instance = await MetaCoinContract.deployed();
    } catch (err) {
      alert(err);
      return;
    }

    const balance  = await instance.getBalance(this.props.web3.eth.accounts[0]);
    this.setState({
      account: this.props.web3.eth.accounts[0],
      accountError: false,
      balance: balance.toString(),
      contractAddress: instance.address,
    });
  }

  public render() {
    return (
    <div>
      <h3>MetaCoin</h3>
      <p>Contract address: {this.state.contractAddress}</p>
      <p>Account: {this.state.accountError ? "No accounts found" : this.state.account}</p>
      <p>Balance: {this.state.balance}</p>
    </div>
    );
  }
}

import * as Web3 from "web3";

let p: Promise<Web3>;

const getWeb3 = (): Promise<Web3> => {
  if (!p) {
    p = new Promise<Web3>((resolve, reject) => {
      // Wait for loading completion to avoid race conditions with web3 injection timing.
      window.addEventListener("load", () => {
        let web3: Web3 = (window as any).web3 as Web3;

        // Checking if Web3 has been injected by the browser (Mist/MetaMask)
        if (typeof web3 !== "undefined") {
          console.log("Using injected web3 provider");
          web3 = new Web3(web3.currentProvider);
        } else {
          // Fallback to localhost if no web3 injection.
          console.log("No web3 instance injected, using Local web3.");
          const provider = new Web3.providers.HttpProvider("http://localhost:8545");
          web3 = new Web3(provider);
        }
        (window as any).web3 = web3;
        resolve(web3);
      });
    });
  }
  return p;
};

export default getWeb3;
